mapper.update:
UPDATE gagu
SET name = #{name}, price = #{price}, kind = #{kind}
WHERE id = #{id} AND member_id = #{memberId}

mapper.select:
SELECT * FROM gagu WHERE name LIKE CONCAT(CONCAT("%", #{keyword}), "%")
ORDER BY name ORDER BY price DESC ORDER BY price ORDER BY name DESC
LIMIT ${pager.offset}, ${pager.perPage}
